import React, { useState } from 'react';
import axios from 'axios';

function App() {
    const [pickupLat, setPickupLat] = useState('');
    const [pickupLon, setPickupLon] = useState('');
    const [dropoffLat, setDropoffLat] = useState('');
    const [dropoffLon, setDropoffLon] = useState('');
    const [time, setTime] = useState('');
    const [result, setResult] = useState('');

    const getCurrentLocation = () => {
        if (navigator.geolocation) {
            navigator.geolocation.getCurrentPosition(
                (position) => {
                    setPickupLat(position.coords.latitude);
                    setPickupLon(position.coords.longitude);
                },
                (error) => {
                    alert("Error getting location: " + error.message);
                }
            );
        } else {
            alert("Geolocation is not supported by this browser.");
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://localhost:5000/predict', {
                pickup_lat: pickupLat,
                pickup_lon: pickupLon,
                dropoff_lat: dropoffLat,
                dropoff_lon: dropoffLon,
                time: time
            });
            setResult(`Predicted fare: ${response.data.fare}`);
        } catch (error) {
            console.error("Error submitting data:", error);
            alert("Error submitting data.");
        }
    };

    return (
        <div className="App">
            <h1>Taxi Fare Prediction</h1>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Pickup Latitude:</label>
                    <input
                        type="text"
                        value={pickupLat}
                        onChange={(e) => setPickupLat(e.target.value)}
                        readOnly
                    />
                    <button type="button" onClick={getCurrentLocation}>
                        Get Current Location
                    </button>
                </div>
                <div>
                    <label>Pickup Longitude:</label>
                    <input
                        type="text"
                        value={pickupLon}
                        onChange={(e) => setPickupLon(e.target.value)}
                        readOnly
                    />
                </div>
                <div>
                    <label>Dropoff Latitude:</label>
                    <input
                        type="text"
                        value={dropoffLat}
                        onChange={(e) => setDropoffLat(e.target.value)}
                    />
                </div>
                <div>
                    <label>Dropoff Longitude:</label>
                    <input
                        type="text"
                        value={dropoffLon}
                        onChange={(e) => setDropoffLon(e.target.value)}
                    />
                </div>
                <div>
                    <label>Time (minutes):</label>
                    <input
                        type="text"
                        value={time}
                        onChange={(e) => setTime(e.target.value)}
                    />
                </div>
                <button type="submit">Predict Fare</button>
            </form>
            {result && <h2>{result}</h2>}
        </div>
    );
}

export default App;
